            if t.titre == titre:
                self.taches.remove(t)
            print("Tâche supprimée")   

    def afficher(self):
        for t in self.taches:
            print(t.titre, t.tags)


if __name__ == "__main__":
    t1 = Tache("Réviser", ["python"])
    t2 = Tache("Projet")

    t2.ajouter_tag("urgent")

    g = GestionTaches()
    g.ajouter(t1)
    g.ajouter(t2)

    g.supprimer("Réviser")
    g.afficher()

